mapper.update:
UPDATE    running_time
SET     
		seat_remain = #{seat_remain}
WHERE  1=1
AND
   seq = #{seq}

mapper.select:

    	      select

                a.seat_name

			from
				ticketing_detail a
   				join running_time b on b.seq = a.running_time_seq
  			    join movie c on c.seq = b.movie_seq
   			    join cinema_theater e on e.seq = b.cinema_theater_seq
  				join cinema_name d on d.seq = e.cinema_name_seq
   				join member g on g.seq = a.member_seq 
   		   where
   		   		 	movie_name = #{movie_name}
 				 and    cinema_type = #{cinema_type}
 				 and 	location_cinema_name = #{location_cinema_name}
 				 and	theater_number = #{theater_number}
 				 and	start_time = #{start_time}
 				 and 	date = #{date}
				 and		a.delYN =0;


    	      	select 
					 a.date

				from
 					  running_time a
 				 join movie b on b.seq = a.movie_seq
  				 join cinema_theater c on c.seq = a.cinema_theater_seq
  				 join cinema_name d on d.seq = c.cinema_name_seq
  				 join cinema e on e.seq = d.cinema_seq
 				 WHERE 1=1
 				 and 	movie_name = #{movie_name}
 				 and    cinema_type = #{cinema_type}
 				 and 	location_cinema_name = #{location_cinema_name}
 				 and	a.delYN =0;


    	      	select DISTINCT
					 d.location_cinema_name

				from
 					  running_time a
 				 join movie b on b.seq = a.movie_seq
  				 join cinema_theater c on c.seq = a.cinema_theater_seq
  				 join cinema_name d on d.seq = c.cinema_name_seq
  				 join cinema e on e.seq = d.cinema_seq
 				 WHERE 1=1
 				 and 	movie_name = #{movie_name}
 				 and    cinema_type = #{cinema_type}
 				 and 	location = #{location}
 				 and	a.delYN =0;

select DISTINCT
e.location
from running_time a join movie b on b.seq = a.movie_seq join cinema_theater c on c.seq = a.cinema_theater_seq join cinema_name d on d.seq = c.cinema_name_seq join cinema e on e.seq = d.cinema_seq WHERE a.delYN = 0;

      SELECT aa.* FROM (   	



		) aa
		LIMIT #{rowNumToShow} OFFSET #{startRnumForMysql}

SELECT a.seq, a.payment_amount, a.delYN, b.date, b.start_time, b.end_time, c.movie_name, d.location_cinema_name, e.theater_number, e.cinema_type, a.seat_name, g.name FROM ticketing_detail a join running_time b on b.seq = a.running_time_seq join movie c on c.seq = b.movie_seq join cinema_theater e on e.seq = b.cinema_theater_seq join cinema_name d on d.seq = e.cinema_name_seq
join member g on g.seq = a.member_seq WHERE
a.seq = #{seq}
